INSERT INTO question (id, answer_type, code, milestone, req, section, text, testing)
  VALUES
    (2001, 'NONE', '1.1', '', '1', '', 'Are firewall and router configuration standards established and implemented to include the following:', ''),
    (2002, 'YES_NO', '1.1.1', '', '1', '', 'Is there a formal process for approving and testing all network connections and changes to the firewall and router configurations?', '<ul><li>Review documented process </li><li>Interview personnel</li><li>Examine network configurations</li></ul>'),
    (2003, 'YES_NO', '1.1.2.a', '', '1', '', 'Is there a current network diagram that documents all connections between the cardholder data environment and other networks, including any wireless networks?', '<ul><li>Review current network diagram</li><li>Examine network configurations</li></ul>'),
    (2004, 'YES_NO', '1.1.2.b', '', '1', '', 'Is there a process to ensure the diagram is kept current?', '<ul><li>Interview responsible personnel</li></ul>'),
    (2005, 'YES_NO', '1.1.3.a', '', '1', '', 'Is there a current diagram that shows all cardholder data flows across systems and networks?', '<ul><li>Review current dataflow diagram</li><li>Examine network configurations.</li></ul>'),
    (2006, 'YES_NO', '1.1.3.b', '', '1', '', 'Is there a process to ensure the diagram is kept current?', '<ul><li>Interview personnel</li></ul>'),
    (2007, 'YES_NO', '1.1.4.a', '', '1', '', 'Is a firewall required and implemented at each Internet connection and between any demilitarized zone (DMZ) and the internal network zone?', '<ul><li>Review firewall configuration standards </li><li>Observe network configurations to verify that a firewall(s) is in place</li></ul>'),
    (2008, 'YES_NO', '1.1.4.b', '', '1', '', 'Is the current network diagram consistent with the firewall configuration standards?', '<ul><li>Compare firewall configuration standards to current network diagram</li></ul>'),
    (2009, 'YES_NO', '1.1.5', '', '1', '', 'Are groups, roles, and responsibilities for logical management of network components assigned and documented in the firewall and router configuration standards?', '<ul><li>Review firewall and router configuration standards </li><li>Interview personnel</li></ul>'),
    (2010, 'YES_NO', '1.1.6.a', '', '1', '', 'Do firewall and router configuration standards include a documented list of services, protocols, and ports, including business justification and approval for each?', '<ul><li>Review firewall and router configuration standards </li></ul>'),
    (2011, 'YES_NO', '1.1.6.b', '', '1', '', 'Are all insecure services, protocols, and ports identified, and are security features documented and implemented for each identified service?', '<ul><li>Review firewall and router configuration standards </li><li>Examine firewall and router configurations</li></ul>'),
    (2012, 'YES_NO', '1.1.7.a', '', '1', '', 'Do firewall and router configuration standards require review of firewall and router rule sets at least every six months? ', '<ul><li>Review firewall and router configuration standards </li></ul>'),
    (2013, 'YES_NO', '1.1.7.b', '', '1', '', 'Are firewall and router rule sets reviewed at least every six months?', '<ul><li>Examine documentation from firewall reviews</li></ul>'),
    (2014, 'NONE', '1.2', '', '1', '', 'Do firewall and router configurations restrict connections between untrusted networks and any system in the cardholder data environment as follows: Note: An “untrusted network” is any network that is external to the networks belonging to the entity under review, and/or which is out of the entity’s ability to control or manage.', ''),
    (2015, 'YES_NO', '1.2.1.a', '', '1', '', 'Is inbound and outbound traffic restricted to that which is necessary for the cardholder data environment? ', '<ul><li>Review firewall and router configuration standards</li><li>Examine firewall and router configurations</li></ul>'),
    (2016, 'YES_NO', '1.2.1.b', '', '1', '', 'Is all other inbound and outbound traffic specifically denied (for example by using an explicit “deny all” or an implicit deny after allow statement)? ', '<ul><li>Review firewall and router configuration standards</li><li>Examine firewall and router configurations</li></ul>'),
    (2017, 'YES_NO', '1.2.2', '', '1', '', 'Are router configuration files secured from unauthorized access and synchronized—for example, the running (or active) configuration matches the start-up configuration (used when machines are booted)?', '<ul><li>Review firewall and router configuration standards</li><li>Examine router configuration files and router configurations</li></ul>'),
    (2018, 'YES_NO', '1.2.3', '', '1', '', 'Are perimeter firewalls installed between all wireless networks and the cardholder data environment, and are these firewalls configured to deny or, if traffic is necessary for business purposes, permit only authorized traffic between the wireless environment and the cardholder data environment?', '<ul><li>Review firewall and router configuration standards</li><li>Examine firewall and router configurations</li></ul>'),
    (2019, 'NONE', '1.3', '', '1', '', 'Is direct public access prohibited between the Internet and any system component in the cardholder data environment, as follows:', ''),
    (2020, 'YES_NO', '1.3.1', '', '1', '', 'Is a DMZ implemented to limit inbound traffic to only system components that provide authorized publicly accessible services, protocols, and ports?', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2021, 'YES_NO', '1.3.2', '', '1', '', 'Is inbound Internet traffic limited to IP addresses within the DMZ?', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2022, 'YES_NO', '1.3.3', '', '1', '', 'Are anti-spoofing measures implemented to detect and block forged sourced IP addresses from entering the network?  (For example, block traffic originating from the internet with an internal address.)', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2023, 'YES_NO', '1.3.4', '', '1', '', 'Is outbound traffic from the cardholder data environment to the Internet explicitly authorized?', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2024, 'YES_NO', '1.3.5', '', '1', '', 'Are only established connections permitted into the network?', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2025, 'YES_NO', '1.3.6', '', '1', '', 'Are system components that store cardholder data (such as a database) placed in an internal network zone, segregated from the DMZ and other untrusted networks?', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2026, 'YES_NO', '1.3.7.a', '', '1', '', 'Are methods in place to prevent the disclosure of private IP addresses and routing information to the Internet? Note: Methods to obscure IP addressing may include, but are not limited to: Network Address Translation (NAT) Placing servers containing cardholder data behind proxy servers/firewalls,  Removal or filtering of route advertisements for private networks that employ registered addressing, Internal use of RFC1918 address space instead of registered addresses. ', '<ul><li>Examine firewall and router configurations</li></ul>'),
    (2027, 'YES_NO', '1.3.7.b', '', '1', '', 'Is any disclosure of private IP addresses and routing information to external entities authorized?', '<ul><li>Examine firewall and router configurations </li><li>Interview personnel </li></ul>'),
    (2028, 'YES_NO', '1.4.a', '', '1', '', 'Is personal firewall software (or equivalent functionality) installed and active on any portable computing devices (including company and/or employee-owned) that connect to the Internet when outside the network (for example, laptops used by employees), and which are also used to access the CDE?', '<ul><li>Review policies and configuration standards </li><li>Examine mobile and/or employee-owned devices</li></ul>'),
    (2029, 'YES_NO', '1.4.b', '', '1', '', 'Is the personal firewall software (or equivalent functionality) configured to specific configuration settings, actively running, and not alterable by users of mobile and/or employee-owned devices?', '<ul><li>Review policies and configuration standards </li><li>Examine mobile and/or employee-owned devices</li></ul>'),
    (2030, 'YES_NO', '1.5', '', '1', '', 'Are security policies and operational procedures for managing firewalls:  Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2031, 'YES_NO', '2.1.a', '', '2', '', 'Are vendor-supplied defaults always changed before installing a system on the network? This applies to ALL default passwords, including but not limited to those used by operating systems, software that provides security services, application and system accounts, point-of-sale (POS) terminals, payment applications, Simple Network Management Protocol (SNMP) community strings, etc.).', '<ul><li>Review policies and procedures</li><li>Examine vendor documentation</li><li>Observe system configurations and account settings</li><li>Interview personnel</li></ul>'),
    (2032, 'YES_NO', '2.1.b', '', '2', '', 'Are unnecessary default accounts removed or disabled before installing a system on the network?', '<ul><li>Review policies and procedures</li><li>Review vendor documentation</li><li>Examine system configurations and account settings</li><li>Interview personnel</li></ul>'),
    (2033, 'NONE', '2.1.1', '', '2', '', 'For wireless environments connected to the cardholder data environment or transmitting cardholder data, are ALL wireless vendor defaults changed at installations, as follows:', ''),
    (2034, 'YES_NO', '2.1.1.a', '', '2', '', 'Are encryption keys changed from default at installation, and changed anytime anyone with knowledge of the keys leaves the company or changes positions?', '<ul><li>Review policies and procedures</li><li>Review vendor documentation</li><li>Interview personnel</li></ul>'),
    (2035, 'YES_NO', '2.1.1.b', '', '2', '', 'Are default SNMP community strings on wireless devices changed at installation?', '<ul><li>Review policies and procedures</li><li>Review vendor documentation</li><li>Interview personnel</li><li>Examine system configurations</li></ul>'),
    (2036, 'YES_NO', '2.1.1.c', '', '2', '', 'Are default passwords/passphrases on access points changed at installation?', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Examine system configurations</li></ul>'),
    (2037, 'YES_NO', '2.1.1.d', '', '2', '', 'Is firmware on wireless devices updated to support strong encryption for authentication and transmission over wireless networks?', '<ul><li>Review policies and procedures</li><li>Review vendor documentation</li><li>Examine system configurations</li></ul>'),
    (2038, 'YES_NO', '2.1.1.e', '', '2', '', 'Are other security-related wireless vendor defaults changed, if applicable?', '<ul><li>Review policies and procedures</li><li>Review vendor documentation</li><li>Examine system configurations</li></ul>'),
    (2039, 'YES_NO', '2.2.a', '', '2', '', 'Are configuration standards developed for all system components and are they consistent with industry-accepted system hardening standards?  Sources of industry-accepted system hardening standards may include, but are not limited to, SysAdmin Audit Network Security (SANS) Institute, National Institute of Standards Technology (NIST), International Organization for Standardization (ISO), and Center for Internet Security (CIS).', '<ul><li>Review system configuration standards</li><li>Review industry-accepted hardening standards</li><li>Review policies and procedures</li><li>Interview personnel</li></ul>'),
    (2040, 'YES_NO', '2.2.b', '', '2', '', 'Are system configuration standards updated as new vulnerability issues are identified, as defined in Requirement 6.1?', '<ul><li>Review policies and procedures</li><li>Interview personnel</li></ul>'),
    (2041, 'YES_NO', '2.2.c', '', '2', '', 'Are system configuration standards applied when new systems are configured? ', '<ul><li>Review policies and procedures</li><li>Interview personnel</li></ul>'),
    (2042, 'YES_NO', '2.2.d', '', '2', '', 'Do system configuration standards include all of the following:  Changing of all vendor-supplied defaults and elimination of unnecessary default accounts? Implementing only one primary function per server to prevent functions that require different security levels from co-existing on the same server? Enabling only necessary services, protocols, daemons, etc., as required for the function of the system? Implementing additional security features for any required services, protocols or daemons that are considered to be insecure? Configuring system security parameters to prevent misuse? Removing all unnecessary functionality, such as scripts, drivers, features, subsystems, file systems, and unnecessary web servers?', '<ul><li>Review system configuration standards</li></ul>'),
    (2043, 'YES_NO', '2.2.1.a', '', '2', '', 'Is only one primary function implemented per server, to prevent functions that require different security levels from co-existing on the same server? For example, web servers, database servers, and DNS should be implemented on separate servers.', '<ul><li>Examine system configurations</li></ul>'),
    (2044, 'YES_NO', '2.2.1.b', '', '2', '', 'If virtualization technologies are used, is only one primary function implemented per virtual system component or device?', '<ul><li>Examine system configurations</li></ul>'),
    (2045, 'YES_NO', '2.2.2.a', '', '2', '', 'Are only necessary services, protocols, daemons, etc. enabled as required for the function of the system (services and protocols not directly needed to perform the device’s specified function are disabled)?', '<ul><li>Review configuration standards</li><li>Examine system configurations</li></ul>'),
    (2046, 'YES_NO', '2.2.2.b', '', '2', '', 'Are all enabled insecure services, daemons, or protocols justified per documented configuration standards?', '<ul><li>Review configuration standards</li><li>Interview personnel </li><li>Examine configuration settings</li><li>Compare enabled services, etc. to documented justifications</li></ul>'),
    (2047, 'YES_NO', '2.2.3', '', '2', '', 'Are additional security features documented and implemented for any required services, protocols or daemons that are considered to be insecure?', '<ul><li>Review configuration standards</li><li>Examine configuration settings</li></ul>'),
    (2048, 'YES_NO', '2.2.4.a', '', '2', '', 'Are system administrators and/or personnel that configure system components knowledgeable about common security parameter settings for those system components?', '<ul><li>Interview personnel</li></ul>'),
    (2049, 'YES_NO', '2.2.4.b', '', '2', '', 'Are common system security parameters settings included in the system configuration standards?', '<ul><li>Review system configuration standards</li></ul>'),
    (2050, 'YES_NO', '2.2.4.c', '', '2', '', 'Are security parameter settings set appropriately on system components?', '<ul><li>Examine system components</li><li>Examine security parameter settings</li><li>Compare settings to system configuration standards</li></ul>'),
    (2051, 'YES_NO', '2.2.5.a', '', '2', '', 'Has all unnecessary functionality—such as scripts, drivers, features, subsystems, file systems, and unnecessary web servers—been removed?', '<ul><li>Examine security parameters on system components</li></ul>'),
    (2052, 'YES_NO', '2.2.5.b', '', '2', '', 'Are enabled functions documented and do they support secure configuration?', '<ul><li>Review documentation</li><li>Examine security parameters on system components</li></ul>'),
    (2053, 'YES_NO', '2.2.5.c', '', '2', '', 'Is only documented functionality present on system components?', '<ul><li>Review documentation</li><li>Examine security parameters on system components</li></ul>'),
    (2054, 'NONE', '2.3', '', '2', '', 'Is non-console administrative access encrypted as follows:', ''),
    (2055, 'YES_NO', '2.3.a', '', '2', '', 'Is all non-console administrative access encrypted with strong cryptography, and is a strong encryption method invoked before the administrator’s password is requested?', '<ul><li>Examine system components</li><li>Examine system configurations </li><li>Observe an administrator log on</li></ul>'),
    (2056, 'YES_NO', '2.3.b', '', '2', '', 'Are system services and parameter files configured to prevent the use of Telnet and other insecure remote login commands?', '<ul><li>Examine system components</li><li>Examine services and files</li></ul>'),
    (2057, 'YES_NO', '2.3.c', '', '2', '', 'Is administrator access to web-based management interfaces encrypted with strong cryptography?', '<ul><li>Examine system components</li><li>Observe an administrator log on</li></ul>'),
    (2058, 'YES_NO', '2.3.d', '', '2', '', 'For the technology in use, is strong cryptography implemented according to industry best practice and/or vendor recommendations?', '<ul><li>Examine system components</li><li>Review vendor documentation</li><li>Interview personnel</li></ul>'),
    (2059, 'YES_NO', '2.4.a', '', '2', '', 'Is an inventory maintained for systems components that are in scope for PCI DSS, including a list of hardware and software components and a description of function/use for each?', '<ul><li>Examine system inventory</li></ul>'),
    (2060, 'YES_NO', '2.4.b', '', '2', '', 'Is the documented inventory kept current?', '<ul><li>Interview personnel</li></ul>'),
    (2061, 'YES_NO', '2.5', '', '2', '', 'Are security policies and operational procedures for managing vendor defaults and other security parameters:  Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2062, 'NONE', '2.6', '', '2', '', 'This requirement applies only to service providers.', ''),
    (2063, 'NONE', '3.1', '', '3', '', 'Are data-retention and disposal policies, procedures, and processes implemented as follows:', ''),
    (2064, 'YES_NO', '3.1.a', '', '3', '', 'Is data storage amount and retention time limited to that required for legal, regulatory, and/or business requirements?', '<ul><li>Review data retention and disposal policies and procedures</li><li>Interview personnel</li></ul>'),
    (2065, 'YES_NO', '3.1.b', '', '3', '', 'Are there defined processes in place for securely deleting cardholder data when no longer needed for legal, regulatory, and/or business reasons?', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Examine deletion mechanism </li></ul>'),
    (2066, 'YES_NO', '3.1.c', '', '3', '', 'Are there specific retention requirements for cardholder data?  For example, cardholder data needs to be held for X period for Y business reasons.', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Examine retention requirements</li></ul>'),
    (2067, 'YES_NO', '3.1.d', '', '3', '', 'Is there a quarterly process for identifying and securely deleting stored cardholder data that exceeds defined retention requirements?', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Observe deletion processes</li></ul>'),
    (2068, 'YES_NO', '3.1.e', '', '3', '', 'Does all stored cardholder data meet the requirements defined in the data-retention policy? ', '<ul><li>Examine files and system records</li></ul>'),
    (2069, 'NONE', '3.2.a', '', '3', '', 'This testing procedure applies only to Issuers.', ''),
    (2070, 'NONE', '3.2.b', '', '3', '', 'This testing procedure applies only to Issuers.', ''),
    (2071, 'YES_NO', '3.2.c', '', '3', '', 'Is sensitive authentication data deleted or rendered unrecoverable upon completion of the authorization process?', '<ul><li>Review policies and procedures</li><li>Examine system configurations</li><li>Examine deletion processes</li></ul>'),
    (2072, 'NONE', '3.2.d', '', '3', '', 'Do all systems adhere to the following requirements regarding non-storage of sensitive authentication data after authorization (even if encrypted):', ''),
    (2073, 'YES_NO', '3.2.1', '', '3', '', 'The full contents of any track (from the magnetic stripe located on the back of a card, equivalent data contained on a chip, or elsewhere) are not stored after authorization? This data is alternatively called full track, track, track 1, track 2, and magnetic-stripe data. Note: In the normal course of business, the following data elements from the magnetic stripe may need to be retained:  The cardholder’s name,  Primary account number (PAN),  Expiration date, and  Service code   To minimize risk, store only these data elements as needed for business.', '<ul><li>Examine data sources including:</li><li>Incoming transaction data</li><li>All logs</li><li>History files</li><li>Trace files</li><li>Database schema</li><li>Database contents</li></ul>'),
    (2074, 'YES_NO', '3.2.2', '', '3', '', 'The card verification code or value (three-digit or four-digit number printed on the front or back of a payment card) is not stored after authorization?', '<ul><li>Examine data sources including:</li><li>Incoming transaction data</li><li>All logs</li><li>History files</li><li>Trace files</li><li>Database schema</li><li>Database contents</li></ul>'),
    (2075, 'YES_NO', '3.2.3', '', '3', '', 'The personal identification number (PIN) or the encrypted PIN block is not stored after authorization?', '<ul><li>Examine data sources including:</li><li>Incoming transaction data</li><li>All logs</li><li>History files</li><li>Trace files</li><li>Database schema</li><li>Database contents</li></ul>'),
    (2076, 'YES_NO', '3.3', '', '3', '', 'Is the PAN masked when displayed (the first six and last four digits are the maximum number of digits to be displayed) such that only personnel with a legitimate business need can see more than the first six/last four digits of the PAN? Note: This requirement does not supersede stricter requirements in place for displays of cardholder data—for example, legal or payment card brand requirements for point-of-sale (POS) receipts.', '<ul><li>Review policies and procedures</li><li>Review roles that need access to displays of full PAN</li><li>Examine system configurations </li><li>Observe displays of PAN</li></ul>'),
    (2077, 'YES_NO', '3.4', '', '3', '', 'Is PAN rendered unreadable anywhere it is stored (including data repositories, portable digital media, backup media, and in audit logs), by using any of the following approaches? One-way hashes based on strong cryptography (hash must be of the entire PAN) Truncation (hashing cannot be used to replace the truncated segment of PAN) Index tokens and pads (pads must be securely stored) Strong cryptography with associated key management processes and procedures. Note: It is a relatively trivial effort for a malicious individual to reconstruct original PAN data if they have access to both the truncated and hashed version of a PAN. Where hashed and truncated versions of the same PAN are present in an entity’s environment, additional controls must be in place to ensure that the hashed and truncated versions cannot be correlated to reconstruct the original PAN.', '<ul><li>Examine vendor documentation</li><li>Examine data repositories</li><li>Examine removable media</li><li>Examine audit logs, including payment application logs</li></ul>'),
    (2078, 'NONE', '3.4.1', '', '3', '', 'If disk encryption (rather than file- or column-level database encryption) is used, is access managed as follows: Note: This requirement applies in addition to all other PCI DSS encryption and key management requirements.', ''),
    (2079, 'YES_NO', '3.4.1.a', '', '3', '', 'Is logical access to encrypted file systems managed separately and independently of native operating system authentication and access control mechanisms (for example, by not using local user account databases or general network login credentials)?', '<ul><li>Examine system configurations</li><li>Observe the authentication process</li></ul>'),
    (2080, 'YES_NO', '3.4.1.b', '', '3', '', 'Are cryptographic keys stored securely (for example, stored on removable media that is adequately protected with strong access controls)?', '<ul><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2081, 'YES_NO', '3.4.1.c', '', '3', '', 'Is cardholder data on removable media encrypted wherever stored? Note: If disk encryption is not used to encrypt removable media, the data stored on this media will need to be rendered unreadable through some other method.', '<ul><li>Examine system configurations</li><li>Observe processes</li></ul>'),
    (2082, 'NONE', '3.5', '', '3', '', 'Are keys used to secure stored cardholder data protected against disclosure and misuse as follows: Note: This requirement applies to keys used to encrypt stored cardholder data, and also applies to key-encrypting keys used to protect data-encrypting keys. Such key-encrypting keys must be at least as strong as the data-encrypting key.', ''),
    (2083, 'NONE', '3.5.1', '', '3', '', 'This requirement applies only to service providers', ''),
    (2084, 'YES_NO', '3.5.2', '', '3', '', 'Is access to cryptographic keys restricted to the fewest number of custodians necessary?', '<ul><li>Examine user access lists</li></ul>'),
    (2085, 'YES_NO', '3.5.3', '', '3', '', 'Are secret and private cryptographic keys used to encrypt/decrypt cardholder data stored in one (or more) of the following forms at all times? Encrypted with a key-encrypting key that is at least as strong as the data-encrypting key, and that is stored separately from the data-encrypting key Within a secure cryptographic device (such as a hardware (host) security module (HSM) or PTS-approved point-of-interaction device) As at least two full-length key components or key shares, in accordance with an industry-accepted method. Note: It is not required that public keys be stored in one of these forms. ', '<ul><li>Review documented procedures</li><li>Examine system configurations and key storage locations, including for key-encrypting keys</li></ul>'),
    (2086, 'YES_NO', '3.5.4', '', '3', '', 'Are cryptographic keys stored in the fewest possible locations?', '<ul><li>Examine key-storage locations</li><li>Observe processes</li></ul>'),
    (2087, 'YES_NO', '3.6.a', '', '3', '', 'Are all key-management processes and procedures fully documented and implemented for cryptographic keys used for encryption of cardholder data?', '<ul><li>Review key-management procedures </li></ul>'),
    (2088, 'NONE', '3.6.b', '', '3', '', 'This testing procedure applies only to service providers. ', ''),
    (2089, 'NONE', '3.6.c', '', '3', '', 'Are key-management processes and procedures implemented to require the following:', ''),
    (2090, 'YES_NO', '3.6.1', '', '3', '', 'Do cryptographic key procedures include the generation of strong cryptographic keys?', '<ul><li>Review key-management procedures</li><li>Observe key-generation method</li></ul>'),
    (2091, 'YES_NO', '3.6.2', '', '3', '', 'Do cryptographic key procedures include secure cryptographic key distribution?', '<ul><li>Review key management procedures</li><li>Observe the key-distribution procedures</li></ul>'),
    (2092, 'YES_NO', '3.6.3', '', '3', '', 'Do cryptographic key procedures include secure cryptographic key storage? ', '<ul><li>Review key-management procedures</li><li>Observe the method for secure storage of keys</li></ul>'),
    (2093, 'YES_NO', '3.6.4', '', '3', '', 'Do cryptographic key procedures include cryptographic key changes for keys that have reached the end of their defined cryptoperiod (for example, after a defined period of time has passed and/or after a certain amount of cipher-text has been produced by a given key), as defined by the associated application vendor or key owner, and based on industry best practices and guidelines (for example, NIST Special Publication 800-57)?', '<ul><li>Review key-management procedures</li><li>Interview personnel</li></ul>'),
    (2094, 'YES_NO', '3.6.5.a', '', '3', '', 'Do cryptographic key procedures include retirement or replacement (for example, archiving, destruction, and/or revocation) of cryptographic keys when the integrity of the key has been weakened (for example, departure of an employee with knowledge of a clear-text key)?', '<ul><li>Review key-management procedures</li><li>Interview personnel</li></ul>'),
    (2095, 'YES_NO', '3.6.5.b', '', '3', '', 'Do cryptographic key procedures include replacement of known or suspected compromised keys? ', '<ul><li>Review key-management procedures</li><li>Interview personnel</li></ul>'),
    (2096, 'YES_NO', '3.6.5.c', '', '3', '', 'If retired or replaced cryptographic keys are retained, are these keys only used for decryption/verification purposes, and not used for encryption operations?', '<ul><li>Review key-management procedures</li><li>Interview personnel</li></ul>'),
    (2097, 'YES_NO', '3.6.6', '', '3', '', 'If manual clear-text key-management operations are used, do cryptographic key procedures include split knowledge and dual control of cryptographic keys as follows: Do split knowledge procedures require that key components are under the control of at least two people who only have knowledge of their own key components? AND Do dual control procedures require that at least two people are required to perform any key management operations and no one person has access to the authentication materials (for example, passwords or keys) of another? Note: Examples of manual key management operations include, but are not limited to: key generation, transmission, loading, storage and destruction.', '<ul><li>Review key-management procedures</li><li>Interview personnel and/or</li><li>Observe processes</li></ul>'),
    (2098, 'YES_NO', '3.6.7', '', '3', '', 'Do cryptographic key procedures include the prevention of unauthorized substitution of cryptographic keys?', '<ul><li>Review procedures</li><li>Interview personnel and/or</li><li>Observe processes</li></ul>'),
    (2099, 'YES_NO', '3.6.8', '', '3', '', 'Are cryptographic key custodians required to formally acknowledge (in writing or electronically) that they understand and accept their key-custodian responsibilities?', '<ul><li>Review procedures</li><li>Review documentation or other evidence</li></ul>'),
    (2100, 'YES_NO', '3.7', '', '3', '', 'Are security policies and operational procedures for protecting stored cardholder data: Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2101, 'YES_NO', '4.1.a', '', '4', '', 'Are strong cryptography and security protocols used to safeguard sensitive cardholder data during transmission over open, public networks?  Note: Examples of open, public networks include but are not limited to the Internet; wireless technologies, including 802.11 and Bluetooth; cellular technologies, for example, Global System for Mobile communications (GSM), Code division multiple access (CDMA); and General Packet Radio Service (GPRS).', '<ul><li>Review documented standards</li><li>Review policies and procedures </li><li>Review all locations where CHD is transmitted or received</li><li>Examine system configurations</li></ul>'),
    (2102, 'YES_NO', '4.1.b', '', '4', '', 'Are only trusted keys and/or certificates accepted?', '<ul><li>Observe inbound and outbound transmissions</li><li>Examine keys and certificates</li></ul>'),
    (2103, 'YES_NO', '4.1.c', '', '4', '', 'Are security protocols implemented to use only secure configurations, and to not support insecure versions or configurations?', '<ul><li>Examine system configurations</li></ul>'),
    (2104, 'YES_NO', '4.1.d', '', '4', '', 'Is the proper encryption strength implemented for the encryption methodology in use (check vendor recommendations/best practices)?', '<ul><li>Review vendor documentation</li><li>Examine system configurations</li></ul>'),
    (2105, 'YES_NO', '4.1.e', '', '4', '', 'For TLS implementations, is TLS enabled whenever cardholder data is transmitted or received?  For example, for browser-based implementations: “HTTPS” appears as the browser Universal Record Locator (URL) protocol, andCardholder data is only requested if “HTTPS” appears as part of the URL.', '<ul><li>Examine system configurations</li></ul>'),
    (2106, 'YES_NO', '4.1.1', '', '4', '', 'Are industry best practices used to implement strong encryption for authentication and transmission for wireless networks transmitting cardholder data or connected to the cardholder data environment?', '<ul><li>Review documented standards</li><li>Review wireless networks</li><li>Examine system configuration settings</li></ul>'),
    (2107, 'YES_NO', '4.2.a', '', '4', '', 'Are PANs rendered unreadable or secured with strong cryptography whenever they are sent via end-user messaging technologies (for example, e-mail, instant messaging, SMS, chat, etc.)? ', '<ul><li>Observe processes</li><li>Review outbound transmissions</li></ul>'),
    (2108, 'YES_NO', '4.2.b', '', '4', '', 'Are policies in place that state that unprotected PANs are not to be sent via end-user messaging technologies?', '<ul><li>Review policies and procedures</li></ul>'),
    (2109, 'YES_NO', '4.3', '', '4', '', 'Are security policies and operational procedures for encrypting transmissions of cardholder data: Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2110, 'YES_NO', '5.1', '', '5', '', 'Is anti-virus software deployed on all systems commonly affected by malicious software?', '<ul><li>Examine system configurations</li></ul>'),
    (2111, 'YES_NO', '5.1.1', '', '5', '', 'Are anti-virus programs capable of detecting, removing, and protecting against all known types of malicious software (for example, viruses, Trojans, worms, spyware, adware, and rootkits)?', '<ul><li>Review vendor documentation</li><li>Examine system configurations</li></ul>'),
    (2112, 'YES_NO', '5.1.2', '', '5', '', 'Are periodic evaluations performed to identify and evaluate evolving malware threats in order to confirm whether those systems considered to not be commonly affected by malicious software continue as such?', '<ul><li>Interview personnel</li></ul>'),
    (2113, 'NONE', '5.2', '', '5', '', 'Are all anti-virus mechanisms maintained as follows:', ''),
    (2114, 'YES_NO', '5.2.a', '', '5', '', 'Are all anti-virus software and definitions kept current?', '<ul><li>Examine policies and procedures</li><li>Examine anti-virus configurations, including the master installation </li><li>Examine system components</li></ul>'),
    (2115, 'YES_NO', '5.2.b', '', '5', '', 'Are automatic updates and periodic scans enabled and being performed?', '<ul><li>Examine anti-virus configurations, including the master installation </li><li>Examine system components</li></ul>'),
    (2116, 'YES_NO', '5.2.c', '', '5', '', 'Are all anti-virus mechanisms generating audit logs, and are logs retained in accordance with PCI DSS Requirement 10.7?', '<ul><li>Examine anti-virus configurations</li><li>Review log retention processes</li></ul>'),
    (2117, 'YES_NO', '5.3', '', '5', '', 'Are all anti-virus mechanisms: Actively running? Unable to be disabled or altered by users? Note: Anti-virus solutions may be temporarily disabled only if there is legitimate technical need, as authorized by management on a case-by-case basis. If anti-virus protection needs to be disabled for a specific purpose, it must be formally authorized. Additional security measures may also need to be implemented for the period of time during which anti-virus protection is not active.', '<ul><li>Examine anti-virus configurations</li><li>Examine system components</li><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2118, 'YES_NO', '5.4', '', '5', '', 'Are security policies and operational procedures for protecting systems against malware:  Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2119, 'YES_NO', '6.1', '', '6', '', 'Is there a process to identify security vulnerabilities, including the following: Using reputable outside sources for vulnerability information?  Assigning a risk ranking to vulnerabilities that includes identification of all “high” risk and “critical” vulnerabilities?  Note: Risk rankings should be based on industry best practices as well as consideration of potential impact. For example, criteria for ranking vulnerabilities may include consideration of the CVSS base score and/or the classification by the vendor, and/or type of systems affected. Methods for evaluating vulnerabilities and assigning risk ratings will vary based on an organization’s environment and risk assessment strategy.  Risk rankings should, at a minimum, identify all vulnerabilities considered to be a “high risk” to the environment. In addition to the risk ranking, vulnerabilities may be considered “critical” if they pose an imminent threat to the environment, impact critical systems, and/or would result in a potential compromise if not addressed. Examples of critical systems may include security systems, public-facing devices and systems, databases, and other systems that store, process or transmit cardholder data.', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Observe processes</li></ul>'),
    (2120, 'YES_NO', '6.2.a', '', '6', '', 'Are all system components and software protected from known vulnerabilities by installing applicable vendor-supplied security patches?', '<ul><li>Review policies and procedures</li></ul>'),
    (2121, 'YES_NO', '6.2.b', '', '6', '', 'Are critical security patches installed within one month of release? Note: Critical security patches should be identified according to the risk ranking process defined in Requirement 6.1.', '<ul><li>Review policies and procedures </li><li>Examine system components</li><li>Compare list of security patches installed to recent vendor patch lists</li></ul>'),
    (2122, 'YES_NO', '6.3.a', '', '6', '', 'Are software- development processes based on industry standards and/or best practices?', '<ul><li>Review software development processes</li><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2123, 'YES_NO', '6.3.b', '', '6', '', 'Is information security included throughout the software-development life cycle?', '<ul><li>Review software development processes</li><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2124, 'YES_NO', '6.3.c', '', '6', '', 'Are software applications developed in accordance with PCI DSS (for example, secure authentication and logging)?', '<ul><li>Review software development processes</li><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2125, 'NONE', '6.3.d', '', '6', '', 'Do software development processes ensure the following at 6.3.1 - 6.3.2:', ''),
    (2126, 'YES_NO', '6.3.1', '', '6', '', 'Are development, test, and/or custom application accounts, user IDs, and passwords removed before applications become active or are released to customers?', '<ul><li>Review software development processes</li><li>Interview personnel</li></ul>'),
    (2127, 'YES_NO', '6.3.2', '', '6', '', 'Is all custom code reviewed prior to release to production or customers to identify any potential coding vulnerability (using either manual or automated processes as follows: Are code changes reviewed by individuals other than the originating code author, and by individuals who are knowledgeable about code review techniques and secure coding practices? Do code reviews ensure code is developed according to secure coding guidelines? Are appropriate corrections are implemented prior to release?  Are code review results are reviewed and approved by management prior to release? Note: This requirement for code reviews applies to all custom code (both internal and public-facing), as part of the system development life cycle. Code reviews can be conducted by knowledgeable internal personnel or third parties. Public-facing web applications are also subject to additional controls, to address ongoing threats and vulnerabilities after implementation, as defined at PCI DSS Requirement 6.6.', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Examine recent changes and change records</li></ul>'),
    (2128, 'NONE', '6.4', '', '6', '', 'Are change control processes and procedures followed for all changes to system components to include the following:', ''),
    (2129, 'YES_NO', '6.4.1.a', '', '6', '', 'Are development/test environments separate from the production environment?', '<ul><li>Review change control processes and procedures</li><li>Examine network documentation and network device configurations</li></ul>'),
    (2130, 'YES_NO', '6.4.1.b', '', '6', '', 'Is access control in place to enforce the separation between the development/test environments and the production environment?', '<ul><li>Review change control processes and procedures</li><li>Examine access control settings</li></ul>'),
    (2131, 'YES_NO', '6.4.2', '', '6', '', 'Is there separation of duties between personnel assigned to the development/test environments and those assigned to the production environment?', '<ul><li>Review change control processes and procedures</li><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2132, 'YES_NO', '6.4.3', '', '6', '', 'Are production data (live PANs) not used for testing or development?', '<ul><li>Review change control processes and procedures </li><li>Observe processes</li><li>Interview personnel</li><li>Examine test data</li></ul>'),
    (2133, 'YES_NO', '6.4.4', '', '6', '', 'Are test data and accounts removed from system components before the system becomes active / goes into production?', '<ul><li>Review change control processes and procedures </li><li>Observe processes</li><li>Interview personnel</li><li>Examine production systems</li></ul>'),
    (2134, 'YES_NO', '6.4.5.a', '', '6', '', 'Are change-control procedures documented and require the following?  Documentation of impact Documented change control approval by authorized parties Functionality testing to verify that the change does not adversely impact the security of the system Back-out procedures', '<ul><li>Review change control processes and procedures</li></ul>'),
    (2135, 'NONE', '6.4.5.b', '', '6', '', 'Are the following performed and documented for all changes:', ''),
    (2136, 'YES_NO', '6.4.5.1', '', '6', '', 'Documentation of impact?', '<ul><li>Trace changes to change control documentation </li><li>Examine change control documentation</li></ul>'),
    (2137, 'YES_NO', '6.4.5.2', '', '6', '', 'Documented approval by authorized parties?', '<ul><li>Trace changes to change control documentation </li><li>Examine change control documentation </li></ul>'),
    (2138, 'YES_NO', '6.4.5.3.a', '', '6', '', 'Functionality testing to verify that the change does not adversely impact the security of the system?', '<ul><li>Trace changes to change control documentation </li><li>Examine change control documentation</li></ul>'),
    (2139, 'YES_NO', '6.4.5.3.b', '', '6', '', 'For custom code changes, testing of updates for compliance with PCI DSS Requirement 6.5 before being deployed into production?', '<ul><li>Trace changes to change control documentation</li><li>Examine change control documentation</li></ul>'),
    (2140, 'YES_NO', '6.4.5.4', '', '6', '', 'Back-out procedures?', '<ul><li>Trace changes to change control documentation </li><li>Examine change control documentation</li></ul>'),
    (2141, 'YES_NO', '6.4.6', '', '6', '', 'Upon completion of a significant change, are all relevant PCI DSS requirements implemented on all new or changed systems and networks, and documentation updated as applicable?', '<ul><li>Trace changes to change control documentation</li><li>Examine change control documentation</li><li>Interview personnel</li><li>Observe affected systems or networks</li></ul>'),
    (2142, 'YES_NO', '6.5.a', '', '6', '', 'Do software-development processes address common coding vulnerabilities?', '<ul><li>Review software-development policies and procedures</li></ul>'),
    (2143, 'YES_NO', '6.5.b', '', '6', '', 'Are developers trained at least annually in up-to-date secure coding techniques, including how to avoid common coding vulnerabilities?', '<ul><li>Examine software-development policies and procedures</li><li>Examine training records</li></ul>'),
    (2144, 'NONE', '6.5.c', '', '6', '', 'Are applications developed based on secure coding guidelines to protect applications from, at a minimum, the following vulnerabilities: Note: The vulnerabilities listed at 6.5.1 through 6.5.10 were current with industry best practices when this version of PCI DSS was published. However, as industry best practices for vulnerability management are update d (for example, the Open Web Application Security Project (OWASP) Guide, SANS CWE Top 25, CERT Secure Coding, etc.), the current best practices must be used for these requirements.', ''),
    (2145, 'YES_NO', '6.5.1', '', '6', '', 'Do coding techniques address injection flaws, particularly SQL injection? Note: Also consider OS Command Injection, LDAP and XPath injection flaws as well as other injection flaws.', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2146, 'YES_NO', '6.5.2', '', '6', '', 'Do coding techniques address buffer overflow vulnerabilities?', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2147, 'YES_NO', '6.5.3', '', '6', '', 'Do coding techniques address insecure cryptographic storage? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2148, 'YES_NO', '6.5.4', '', '6', '', 'Do coding techniques address insecure communications? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2149, 'YES_NO', '6.5.5', '', '6', '', 'Do coding techniques address improper error handling? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2150, 'YES_NO', '6.5.6', '', '6', '', 'Do coding techniques address all “high risk” vulnerabilities identified in the vulnerability identification process (as defined in PCI DSS Requirement 6.1)? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2151, 'YES_NO', '6.5.7', '', '6', '', 'Do coding techniques address cross-site scripting (XSS) vulnerabilities? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2152, 'YES_NO', '6.5.8', '', '6', '', 'Do coding techniques address improper access control such as insecure direct object references, failure to restrict URL access, directory traversal, and failure to restrict user access to functions? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2153, 'YES_NO', '6.5.9', '', '6', '', 'Do coding techniques address cross-site request forgery (CSRF)? ', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2154, 'YES_NO', '6.5.10', '', '6', '', 'Do coding techniques address broken authentication and session management?', '<ul><li>Examine software-development policies and procedures</li><li>Interview responsible personnel</li></ul>'),
    (2155, 'YES_NO', '6.6', '', '6', '', 'For public-facing web applications, are new threats and vulnerabilities addressed on an ongoing basis, and are these applications protected against known attacks by applying either of the following methods?  Reviewing public-facing web applications via manual or automated application vulnerability security assessment tools or methods, as follows: At least annuallyAfter any changesBy an organization that specializes in application securityThat, at a minimum, all vulnerabilities in Requirement 6.5 are included in the assessment That all vulnerabilities are correctedThat the application is re-evaluated after the corrections Note: This assessment is not the same as the vulnerability scans performed for Requirement 11.2. – OR –  Installing an automated technical solution that detects and prevents web-based attacks (for example, a web-application firewall) as follows:  Is situated in front of public-facing web applications to detect and prevent web-based attacks.Is actively running and up to date as applicable.Is generating audit logs. Is configured to either block web-based attacks, or generate an alert that is immediately investigated.', '<ul><li>Review documented processes</li><li>Interview personnel</li><li>Examine records of application security assessments</li><li>Examine system configuration settings</li></ul>'),
    (2156, 'YES_NO', '6.7', '', '6', '', 'Are security policies and operational procedures for developing and maintaining secure systems and applications: Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2157, 'NONE', '7.1', '', '7', '', 'Is access to system components and cardholder data limited to only those individuals whose jobs require such access, as follows: ', ''),
    (2158, 'YES_NO', '7.1.a', '', '7', '', 'Is there a written policy for access control that incorporates the following?  Defining access needs and privilege assignments for each role  Restriction of access to privileged user IDs to least privileges necessary to perform job responsibilities,  Assignment of access based on individual personnel’s job classification and function  Documented approval (electronically or in writing) by authorized parties for all access, including listing of specific privileges approved', '<ul><li>Examine written access control policy</li></ul>'),
    (2159, 'YES_NO', '7.1.1', '', '7', '', 'Are access needs for each role defined, including: System components and data resources that each role needs to access for their job function? Level of privilege required (for example, user, administrator, etc.) for accessing resources?', '<ul><li>Examine roles and access needs </li></ul>'),
    (2160, 'YES_NO', '7.1.2', '', '7', '', 'Is access to privileged user IDs restricted as follows:  To least privileges necessary to perform job responsibilities? Assigned only to roles that specifically require that privileged access?', '<ul><li>Interview personnel</li><li>Interview management</li><li>Review privileged user IDs</li></ul>'),
    (2161, 'YES_NO', '7.1.3', '', '7', '', 'Is access assigned based on individual personnel’s job classification and function?', '<ul><li>Interview management</li><li>Review user IDs</li></ul>'),
    (2162, 'YES_NO', '7.1.4', '', '7', '', 'Is documented approval by authorized parties required, specifying required privileges?', '<ul><li>Review user IDs</li><li>Compare with documented approvals</li><li>Compare assigned privileges with documented approvals </li></ul>'),
    (2163, 'NONE', '7.2', '', '7', '', 'Is an access control system(s) in place for system components to restrict access based on a user’s need to know, and is it set to “deny all” unless specifically allowed, as follows:', ''),
    (2164, 'YES_NO', '7.2.1', '', '7', '', 'Is the access control system(s) in place on all system components?', '<ul><li>Review vendor documentation</li><li>Examine configuration settings</li></ul>'),
    (2165, 'YES_NO', '7.2.2', '', '7', '', 'Is the access control system(s) configured to enforce privileges assigned to individuals based on job classification and function?', '<ul><li>Review vendor documentation</li><li>Examine configuration settings</li></ul>'),
    (2166, 'YES_NO', '7.2.3', '', '7', '', 'Does the access control system(s) have a default “deny-all” setting?', '<ul><li>Review vendor documentation</li><li>Examine configuration settings</li></ul>'),
    (2167, 'YES_NO', '7.3', '', '7', '', 'Are security policies and operational procedures for restricting access to cardholder data: Documented In use Known to all affected parties?', '<ul><li>Examine security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2168, 'NONE', '8.1', '', '8', '', 'Are policies and procedures for user identification management controls defined and in place for non-consumer users and administrators on all system components, as follows:', ''),
    (2169, 'YES_NO', '8.1.1', '', '8', '', 'Are all users assigned a unique ID before allowing them to access system components or cardholder data?', '<ul><li>Review password procedures</li><li>Interview personnel</li></ul>'),
    (2170, 'YES_NO', '8.1.2', '', '8', '', 'Are additions, deletions, and modifications of user IDs, credentials, and other identifier objects controlled such that user IDs are implemented only as authorized (including with specified privileges)?', '<ul><li>Review password procedures</li><li>Examine privileged and general user IDs and associated authorizations </li><li>Observe system settings</li></ul>'),
    (2171, 'YES_NO', '8.1.3', '', '8', '', 'Is access for any terminated users immediately deactivated or removed?', '<ul><li>Review password procedures</li><li>Examine terminated users accounts </li><li>Review current access lists</li><li>Observe returned physical authentication devices</li></ul>'),
    (2172, 'YES_NO', '8.1.4', '', '8', '', 'Are inactive user accounts either removed or disabled within 90 days?', '<ul><li>Review password procedures</li><li>Observe user accounts</li></ul>'),
    (2173, 'YES_NO', '8.1.5.a', '', '8', '', 'Are accounts used by third parties to access, support, or maintain system components via remote access enabled only during the time period needed and disabled when not in use?', '<ul><li>Review password procedures</li><li>Interview personnel</li><li>Observe processes </li></ul>'),
    (2174, 'YES_NO', '8.1.5.b', '', '8', '', 'Are third party remote access accounts monitored when in use?', '<ul><li>Interview personnel</li><li>Observe processes</li></ul>'),
    (2175, 'YES_NO', '8.1.6.a', '', '8', '', 'Are repeated access attempts limited by locking out the user ID after no more than six attempts?', '<ul><li>Review password procedures</li><li>Examine system configuration settings</li></ul>'),
    (2176, 'NONE', '8.1.6.b', '', '8', '', 'This testing procedure applies only to service providers. ', ''),
    (2177, 'YES_NO', '8.1.7', '', '8', '', 'Once a user account is locked out, is the lockout duration set to a minimum of 30 minutes or until an administrator enables the user ID?', '<ul><li>Review password procedures</li><li>Examine system configuration settings</li></ul>'),
    (2178, 'YES_NO', '8.1.8', '', '8', '', 'If a session has been idle for more than 15 minutes, are users required to re-authenticate (for example, re-enter the password) to re-activate the terminal or session?', '<ul><li>Review password procedures</li><li>Examine system configuration settings</li></ul>'),
    (2179, 'YES_NO', '8.2', '', '8', '', 'In addition to assigning a unique ID, is one or more of the following methods employed to authenticate all users? Something you know, such as a password or passphrase Something you have, such as a token device or smart card Something you are, such as a biometric', '<ul><li>Review password procedures</li><li>Observe authentication processes</li></ul>'),
    (2180, 'YES_NO', '8.2.1.a', '', '8', '', 'Is strong cryptography used to render all authentication credentials (such as passwords/passphrases) unreadable during transmission and storage on all system components? ', '<ul><li>Review password procedures</li><li>Review vendor documentation</li><li>Examine system configuration settings</li><li>Observe password files</li><li>Observe data transmissions</li></ul>'),
    (2181, 'NONE', '8.2.1.b', '', '8', '', 'This testing procedure applies only to service providers. ', ''),
    (2182, 'YES_NO', '8.2.2', '', '8', '', 'Is user identity verified before modifying any authentication credential (for example, performing password resets, provisioning new tokens, or generating new keys)? ', '<ul><li>Review authentication procedures</li><li>Observe personnel </li></ul>'),
    (2183, 'YES_NO', '8.2.3.a', '', '8', '', 'Are user password parameters configured to require passwords/passphrases meet the following? A minimum password length of at least seven characters Contain both numeric and alphabetic characters Alternatively, the passwords/passphrases must have complexity and strength at least equivalent to the parameters specified above. ', '<ul><li>Examine system configuration settings to verify password parameters </li></ul>'),
    (2184, 'NONE', '8.2.3.b', '', '8', '', 'This testing procedure applies only to service providers. ', ''),
    (2185, 'YES_NO', '8.2.4.a', '', '8', '', 'Are user passwords/passphrases changed at least once every 90 days?', '<ul><li>Review password procedures</li><li>Examine system configuration settings</li></ul>'),
    (2186, 'NONE', '8.2.4.b', '', '8', '', 'This testing procedure applies only to service providers. ', ''),
    (2187, 'YES_NO', '8.2.5.a', '', '8', '', 'Must an individual submit a new password/passphrase that is different from any of the last four passwords/passphrases he or she has used?', '<ul><li>Review password procedures</li><li>Sample system components</li><li>Examine system configuration settings</li></ul>'),
    (2188, 'NONE', '8.2.5.b', '', '8', '', 'This testing procedure applies only to service providers. ', ''),
    (2189, 'YES_NO', '8.2.6', '', '8', '', 'Are passwords/passphrases set to a unique value for each user for first-time use and upon reset, and must each user change their password immediately after the first use?', '<ul><li>Review password procedures</li><li>Examine system configuration settings</li><li>Observe security personnel</li></ul>'),
    (2190, 'NONE', '8.3', '', '8', '', 'Is all individual non-console administrative access and all remote access to the CDE secured using multi-factor authentication as follows: Note: Multi-factor authentication requires that a minimum of two of the three authentication methods (see PCI DSS Requirement 8.2 for descriptions of authentication methods) be used for authentication. Using one factor twice (for example, using two separate passwords) is not considered multi-factor authentication.', ''),
    (2191, 'YES_NO', '8.3.1', '', '8', '', 'Is multi-factor authentication incorporated for all non-console access into the CDE for personnel with administrative access?', '<ul><li>Examine system configurations</li><li>Observe administrator logging into CDE</li></ul>'),
    (2192, 'YES_NO', '8.3.2', '', '8', '', 'Is multi-factor authentication incorporated for all remote network access (both user and administrator, and including third party access for support or maintenance) originating from outside the entity’s network?', '<ul><li>Examine system configurations </li><li>Observe personnel connecting remotely</li></ul>'),
    (2193, 'YES_NO', '8.4.a', '', '8', '', 'Are authentication policies and procedures documented and communicated to all users?', '<ul><li>Review policies and procedures</li><li>Review distribution method</li><li>Interview personnel</li><li>Interview users</li></ul>'),
    (2194, 'YES_NO', '8.4.b', '', '8', '', 'Do authentication policies and procedures include the following?  Guidance on selecting strong authentication credentials Guidance for how users should protect their authentication credentials Instructions not to reuse previously used passwords Instructions that users should change passwords if there is any suspicion the password could be compromised', '<ul><li>Review policies and procedures</li><li>Review documentation provided to users</li></ul>'),
    (2195, 'YES_NO', '8.5', '', '8', '', 'Are group, shared, or generic accounts, passwords, or other authentication methods prohibited as follows:  Generic user IDs and accounts are disabled or removed; Shared user IDs for system administration activities and other critical functions do not exist; and Shared and generic user IDs are not used to administer any system components?', '<ul><li>Review policies and procedures</li><li>Examine user ID lists</li><li>Interview personnel</li></ul>'),
    (2196, 'NONE', '8.5.1', '', '8', '', 'This requirement applies only to service providers. ', ''),
    (2197, 'YES_NO', '8.6', '', '8', '', 'Where other authentication mechanisms are used (for example, physical or logical security tokens, smart cards, and certificates, etc.), is the use of these mechanisms assigned as follows? Authentication mechanisms must be assigned to an individual account and not shared among multiple accounts Physical and/or logical controls must be in place to ensure only the intended account can use that mechanism to gain access', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Examine system configuration settings and/or physical controls</li></ul>'),
    (2198, 'NONE', '8.7', '', '8', '', 'Is all access to any database containing cardholder data (including access by applications, administrators, and all other users) restricted as follows: ', ''),
    (2199, 'YES_NO', '8.7.a', '', '8', '', 'Is all user access to, user queries of, and user actions on (for example, move, copy, delete), the database through programmatic methods only (for example, through stored procedures)?', '<ul><li>Review database authentication policies and procedures </li><li>Examine database and application configuration settings</li></ul>'),
    (2200, 'YES_NO', '8.7.b', '', '8', '', 'Is user direct access to or queries to of databases restricted to database administrators?', '<ul><li>Review database authentication policies and procedures </li><li>Examine database access control settings</li><li>Examine database application configuration settings</li></ul>'),
    (2201, 'YES_NO', '8.7.c', '', '8', '', 'Are application IDs only able to be used by the applications (and not by individual users or other processes)?', '<ul><li>Review database authentication policies and procedures </li><li>Examine database access control settings</li><li>Examine database application configuration settings</li></ul>'),
    (2202, 'YES_NO', '8.8', '', '8', '', 'Are security policies and operational procedures for identification and authentication: Documented In use Known to all affected parties?', '<ul><li>Examine security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2203, 'YES_NO', '9.1', '', '9', '', 'Are appropriate facility entry controls in place to limit and monitor physical access to systems in the cardholder data environment?', '<ul><li>Observe physical access controls</li><li>Observe personnel</li></ul>'),
    (2204, 'YES_NO', '9.1.1.a', '', '9', '', 'Are either video cameras or access-control mechanisms (or both) in place to monitor individual physical access to sensitive areas? Note: “Sensitive areas” refers to any data center, server room, or any area that houses systems that store, process, or transmit cardholder data. This excludes public-facing areas where only point-of-sale terminals are present such as the cashier areas in a retail store.', '<ul><li>Review policies and procedures</li><li>Observe physical monitoring mechanisms</li><li>Observe security features</li></ul>'),
    (2205, 'YES_NO', '9.1.1.b', '', '9', '', 'Are either video cameras or access-control mechanisms (or both) protected from tampering or disabling? ', '<ul><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2206, 'YES_NO', '9.1.1.c', '', '9', '', 'Is data collected from video cameras and/or access control mechanisms reviewed and correlated with other entries?', '<ul><li>Review policies and procedures</li><li>Interview security personnel</li></ul>'),
    (2207, 'YES_NO', '9.1.1.d', '', '9', '', 'Is data collected from video cameras and/or access control mechanisms stored for at least three months unless otherwise restricted by law?', '<ul><li>Review data retention processes</li><li>Observe data storage</li><li>Interview security personnel</li></ul>'),
    (2208, 'YES_NO', '9.1.2', '', '9', '', 'Are physical and/or logical controls in place to restrict access to publicly accessible network jacks?  For example, network jacks located in public areas and areas accessible to visitors could be disabled and only enabled when network access is explicitly authorized. Alternatively, processes could be implemented to ensure that visitors are escorted at all times in areas with active network jacks. ', '<ul><li>Review policies and procedures </li><li>Interview personnel</li><li>Observe locations </li></ul>'),
    (2209, 'YES_NO', '9.1.3', '', '9', '', 'Is physical access to wireless access points, gateways, handheld devices, networking/communications hardware, and telecommunication lines restricted?', '<ul><li>Review policies and procedures </li><li>Interview personnel</li><li>Observe devices</li></ul>'),
    (2210, 'YES_NO', '9.2.a', '', '9', '', 'Are procedures developed to easily distinguish between onsite personnel and visitors, which include: Identifying onsite personnel and visitors (for example, assigning badges),  Changing access requirements, and  Revoking terminated onsite personnel and expired visitor identification (such as ID badges) For the purposes of Requirement 9, “onsite personnel” refers to full-time and part-time employees, temporary employees, contractors and consultants who are physically present on the entity’s premises. A “visitor” refers to a vendor, guest of any onsite personnel, service workers, or anyone who needs to enter the facility for a short duration, usually not more than one day.', '<ul><li>Review policies and procedures</li><li>Interview personnel</li><li>Observe identification methods (e.g. badges) </li><li>Observe visitor processes</li></ul>'),
    (2211, 'YES_NO', '9.2.b', '', '9', '', 'Do identification methods (such as ID badges) clearly identify visitors and easily distinguish between onsite personnel and visitors?', '<ul><li>Observe identification methods</li></ul>'),
    (2212, 'YES_NO', '9.2.c', '', '9', '', 'Is access to the badge system limited to authorized personnel?', '<ul><li>Observe physical controls and access controls for the badge system</li></ul>'),
    (2213, 'YES_NO', '9.3', '', '9', '', 'Is physical access to sensitive areas controlled for onsite personnel, as follows: Is access authorized and based on individual job function? Is access revoked immediately upon termination Upon termination, are all physical access mechanisms, such as keys, access cards, etc., returned or disabled?', '<ul><li>Interview personnel</li><li>Examine access control lists </li><li>Observe onsite personnel</li><li>Compare lists of terminated employees to access control lists</li></ul>'),
    (2214, 'NONE', '9.4', '', '9', '', 'Is visitor identification and access handled as follows:', ''),
    (2215, 'YES_NO', '9.4.1', '', '9', '', 'Are visitors authorized before entering, and escorted at all times within, areas where cardholder data is processed or maintained?', '<ul><li>Review policies and procedures</li><li>Observe visitor processes including how access is controlled</li><li>Interview personnel</li><li>Observe visitors and badge use</li></ul>'),
    (2216, 'YES_NO', '9.4.2.a', '', '9', '', 'Are visitors identified and given a badge or other identification that visibly distinguishes the visitors from onsite personnel?', '<ul><li>Observe badge use of personnel and visitors</li><li>Examine identification</li></ul>'),
    (2217, 'YES_NO', '9.4.2.b', '', '9', '', 'Do visitor badges or other identification expire?', '<ul><li>Observe process</li><li>Examine identification</li></ul>'),
    (2218, 'YES_NO', '9.4.3', '', '9', '', 'Are visitors asked to surrender the badge or other identification before leaving the facility or at the date of expiration?', '<ul><li>Observe processes</li><li>Observe visitors leaving facility</li></ul>'),
    (2219, 'YES_NO', '9.4.4.a', '', '9', '', 'Is a visitor log in use to record physical access to the facility as well as for computer rooms and data centers where cardholder data is stored or transmitted?', '<ul><li>Review policies and procedures</li><li>Examine the visitor log</li><li>Observe visitor processes</li><li>Examine log retention</li></ul>'),
    (2220, 'YES_NO', '9.4.4.b', '', '9', '', 'Does the visitor log contain the visitor’s name, the firm represented, and the onsite personnel authorizing physical access?', '<ul><li>Review policies and procedures</li><li>Examine the visitor log </li></ul>'),
    (2221, 'YES_NO', '9.4.4.c', '', '9', '', 'Is the visitor log retained for at least three months?', '<ul><li>Review policies and procedures</li><li>Examine visitor log retention</li></ul>'),
    (2222, 'YES_NO', '9.5', '', '9', '', 'Are all media physically secured (including but not limited to computers, removable electronic media, paper receipts, paper reports, and faxes)? For purposes of Requirement 9, “media” refers to all paper and electronic media containing cardholder data.', '<ul><li>Review policies and procedures for physically securing media</li><li>Interview personnel</li></ul>'),
    (2223, 'YES_NO', '9.5.1', '', '9', '', 'Is the location where media back-ups are stored reviewed at least annually to confirm storage is secure?', '<ul><li>Review policies and procedures for reviewing offsite media locations</li><li>Interview security personnel</li></ul>'),
    (2224, 'YES_NO', '9.6.a', '', '9', '', 'Is strict control maintained over the internal or external distribution of any kind of media?', '<ul><li>Review policies and procedures for distribution of media</li></ul>'),
    (2225, 'NONE', '9.6.b', '', '9', '', 'Do controls include the following:', ''),
    (2226, 'YES_NO', '9.6.1', '', '9', '', 'Is media classified so the sensitivity of the data can be determined?', '<ul><li>Review policies and procedures for media classification</li><li>Interview security personnel</li></ul>'),
    (2227, 'YES_NO', '9.6.2', '', '9', '', 'Is media sent by secured courier or other delivery method that can be accurately tracked?', '<ul><li>Interview personnel</li><li>Examine media distribution tracking logs and documentation </li></ul>'),
    (2228, 'YES_NO', '9.6.3', '', '9', '', 'Is management approval obtained prior to moving the media (especially when media is distributed to individuals)?', '<ul><li>Interview personnel</li><li>Examine media distribution tracking logs and documentation</li></ul>'),
    (2229, 'YES_NO', '9.7', '', '9', '', 'Is strict control maintained over the storage and accessibility of media?', '<ul><li>Review policies and procedures</li></ul>'),
    (2230, 'YES_NO', '9.7.1.a', '', '9', '', 'Are inventory logs of all media properly maintained?', '<ul><li>Examine inventory logs</li></ul>'),
    (2231, 'YES_NO', '9.7.1.b', '', '9', '', 'Are periodic media inventories conducted at least annually?', '<ul><li>Examine inventory logs</li><li>Interview personnel</li></ul>'),
    (2232, 'YES_NO', '9.8.a', '', '9', '', 'Is all media destroyed when it is no longer needed for business or legal reasons?', '<ul><li>Review periodic media destruction policies and procedures</li></ul>'),
    (2233, 'YES_NO', '9.8.b', '', '9', '', 'Is there a periodic media destruction policy that defines requirements for the following? Hard-copy materials must be crosscut shredded, incinerated, or pulped such that there is reasonable assurance the hard-copy materials cannot be reconstructed.  Storage containers used for materials that are to be destroyed must be secured.  Cardholder data on electronic media must be rendered unrecoverable (e.g. via a secure wipe program in accordance with industry-accepted standards for secure deletion, or by physically destroying the media).', '<ul><li>Review periodic media destruction policies and procedures</li></ul>'),
    (2234, 'NONE', '9.8.c', '', '9', '', 'Is media destruction performed as follows:', ''),
    (2235, 'YES_NO', '9.8.1.a', '', '9', '', 'Are hardcopy materials cross-cut shredded, incinerated, or pulped so that cardholder data cannot be reconstructed?', '<ul><li>Interview personnel</li><li>Examine procedures</li><li>Observe processes</li></ul>'),
    (2236, 'YES_NO', '9.8.1.b', '', '9', '', 'Are storage containers used for materials that contain information to be destroyed secured to prevent access to the contents? ', '<ul><li>Examine security of storage containers</li></ul>'),
    (2237, 'YES_NO', '9.8.2', '', '9', '', 'Is cardholder data on electronic media rendered unrecoverable (e.g. via a secure wipe program in accordance with industry-accepted standards for secure deletion, or otherwise by physically destroying the media), so that cardholder data cannot be reconstructed?', '<ul><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2238, 'NONE', '9.9', '', '9', '', 'Are devices that capture payment card data via direct physical interaction with the card protected against tampering and substitution as follows? Note: This requirement applies to card-reading devices used in card-present transactions (that is, card swipe or dip) at the point of sale. This requirement is not intended to apply to manual key-entry components such as computer keyboards and POS keypads.', ''),
    (2239, 'YES_NO', '9.9.a', '', '9', '', 'Do policies and procedures require that a list of such devices be maintained?', '<ul><li>Review policies and procedures</li></ul>'),
    (2240, 'YES_NO', '9.9.b', '', '9', '', 'Do policies and procedures require that devices are periodically inspected to look for tampering or substitution?', '<ul><li>Review policies and procedures</li></ul>'),
    (2241, 'YES_NO', '9.9.c', '', '9', '', 'Do policies and procedures require that personnel are trained to be aware of suspicious behavior and to report tampering or substitution of devices?', '<ul><li>Review policies and procedures</li></ul>'),
    (2242, 'YES_NO', '9.9.1.a', '', '9', '', 'Does the list of devices include the following? Make, model of device  Location of device (for example, the address of the site or facility where the device is located)  Device serial number or other method of unique identification', '<ul><li>Examine the list of devices</li></ul>'),
    (2243, 'YES_NO', '9.9.1.b', '', '9', '', 'Is the list accurate and up to date?', '<ul><li>Observe devices and device locations and compare to list</li></ul>'),
    (2244, 'YES_NO', '9.9.1.c', '', '9', '', 'Is the list of devices updated when devices are added, relocated, decommissioned, etc.?', '<ul><li>Interview personnel</li></ul>'),
    (2245, 'YES_NO', '9.9.2.a', '', '9', '', 'Are device surfaces periodically inspected to detect tampering (for example, addition of card skimmers to devices), or substitution (for example, by checking the serial number or other device characteristics to verify it has not been swapped with a fraudulent device) as follows? Note: Examples of signs that a device might have been tampered with or substituted include unexpected attachments or cables plugged into the device, missing or changed security labels, broken or differently colored casing, or changes to the serial number or other external markings.', '<ul><li>Interview personnel</li><li>Observe inspection processes and compare to defined processes</li></ul>'),
    (2246, 'YES_NO', '9.9.2.b', '', '9', '', 'Are personnel aware of procedures for inspecting devices?', '<ul><li>Interview personnel</li></ul>'),
    (2247, 'NONE', '9.9.3', '', '9', '', 'Are personnel trained to be aware of attempted tampering or replacement of devices, to include the following? ', ''),
    (2248, 'YES_NO', '9.9.3.a', '', '9', '', 'Do training materials for personnel at point-of-sale locations include the following?  Verify the identity of any third-party persons claiming to be repair or maintenance personnel, prior to granting them access to modify or troubleshoot devices. Do not install, replace, or return devices without verification.  Be aware of suspicious behavior around devices (for example, attempts by unknown persons to unplug or open devices). Report suspicious behavior and indications of device tampering or substitution to appropriate personnel (for example, to a manager or security officer).', '<ul><li>Review training materials</li></ul>'),
    (2249, 'YES_NO', '9.9.3.b', '', '9', '', 'Have personnel at point-of-sale locations received training, and are they aware of procedures to detect and report attempted tampering or replacement of devices?', '<ul><li>Interview personnel at POS locations</li></ul>'),
    (2250, 'YES_NO', '9.10', '', '9', '', 'Are security policies and operational procedures for restricting physical access to cardholder data: Documented In use Known to all affected parties?', '<ul><li>Examine security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2251, 'YES_NO', '10.1.a', '', '10', '', 'Are audit trails enabled and active for system components?', '<ul><li>Observe processes</li><li>Interview system administrator</li></ul>'),
    (2252, 'YES_NO', '10.1.b', '', '10', '', 'Is access to system components linked to individual users?', '<ul><li>Observe processes</li><li>Interview system administrator</li></ul>'),
    (2253, 'NONE', '10.2', '', '10', '', 'Are automated audit trails implemented for all system components to reconstruct the following events:', ''),
    (2254, 'YES_NO', '10.2.1', '', '10', '', 'All individual user accesses to cardholder data?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2255, 'YES_NO', '10.2.2', '', '10', '', 'All actions taken by any individual with root or administrative privileges?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2256, 'YES_NO', '10.2.3', '', '10', '', 'Access to all audit trails?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2257, 'YES_NO', '10.2.4', '', '10', '', 'Invalid logical access attempts?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2258, 'YES_NO', '10.2.5', '', '10', '', 'Use of and changes to identification and authentication mechanisms–including but not limited to creation of new accounts and elevation of privileges – and all changes, additions, or deletions to accounts with root or administrative privileges?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2259, 'YES_NO', '10.2.6', '', '10', '', 'Initialization, stopping, or pausing of the audit logs?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2260, 'YES_NO', '10.2.7', '', '10', '', 'Creation and deletion of system-level objects?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2261, 'NONE', '10.3', '', '10', '', 'Are the following audit trail entries recorded for all system components for each event:', ''),
    (2262, 'YES_NO', '10.3.1', '', '10', '', 'User identification?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2263, 'YES_NO', '10.3.2', '', '10', '', 'Type of event?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2264, 'YES_NO', '10.3.3', '', '10', '', 'Date and time?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2265, 'YES_NO', '10.3.4', '', '10', '', 'Success or failure indication?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2266, 'YES_NO', '10.3.5', '', '10', '', 'Origination of event?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2267, 'YES_NO', '10.3.6', '', '10', '', 'Identity or name of affected data, system component, or resource?', '<ul><li>Interview personnel</li><li>Observe audit logs</li><li>Examine audit log settings</li></ul>'),
    (2268, 'YES_NO', '10.4', '', '10', '', 'Are all critical system clocks and times synchronized through use of time synchronization technology, and is the technology kept current?   Note: One example of time synchronization technology is Network Time Protocol (NTP).', '<ul><li>Review time configuration standards and processes</li></ul>'),
    (2269, 'NONE', '10.4.1', '', '10', '', 'Are the following processes implemented for critical systems to have the correct and consistent time:', ''),
    (2270, 'YES_NO', '10.4.1.a', '', '10', '', 'Do only designated central time server(s) receive time signals from external sources, and are time signals from external sources based on International Atomic Time or UTC?', '<ul><li>Review time configuration standards and processes </li><li>Examine time-related system parameters </li></ul>'),
    (2271, 'YES_NO', '10.4.1.b', '', '10', '', 'Where there is more than one designated time server, do the time servers peer with each other to keep accurate time?', '<ul><li>Review time configuration standards and processes </li><li>Examine time-related system parameters</li></ul>'),
    (2272, 'YES_NO', '10.4.1.c', '', '10', '', 'Do systems receive time only from designated central time server(s)?', '<ul><li>Review time configuration standards and processes </li><li>Examine time-related system parameters</li></ul>'),
    (2273, 'YES_NO', '10.4.2.a', '', '10', '', 'Is time data is protected as follows: Is access to time data restricted to only personnel with a business need to access time data?', '<ul><li>Examine system configurations and time-synchronization settings</li></ul>'),
    (2274, 'YES_NO', '10.4.2.b', '', '10', '', 'Are changes to time settings on critical systems logged, monitored, and reviewed?', '<ul><li>Examine system configurations and time-synchronization settings and logs</li></ul>'),
    (2275, 'YES_NO', '10.4.3', '', '10', '', 'Are time settings received from specific, industry-accepted time sources? (This is to prevent a malicious individual from changing the clock).  Optionally, those updates can be encrypted with a symmetric key, and access control lists can be created that specify the IP addresses of client machines that will be provided with the time updates (to prevent unauthorized use of internal time servers).', '<ul><li>Examine system configurations </li></ul>'),
    (2276, 'NONE', '10.5', '', '10', '', 'Are audit trails secured so they cannot be altered, as follows: ', ''),
    (2277, 'YES_NO', '10.5.1', '', '10', '', 'Is viewing of audit trails limited to those with a job-related need?', '<ul><li>Interview system administrators</li><li>Examine system configurations and permissions</li></ul>'),
    (2278, 'YES_NO', '10.5.2', '', '10', '', 'Are audit trail files protected from unauthorized modifications via access control mechanisms, physical segregation, and/or network segregation?', '<ul><li>Interview system administrators</li><li>Examine system configurations and permissions</li></ul>'),
    (2279, 'YES_NO', '10.5.3', '', '10', '', 'Are audit trail files promptly backed up to a centralized log server or media that is difficult to alter?', '<ul><li>Interview system administrators</li><li>Examine system configurations and permissions</li></ul>'),
    (2280, 'YES_NO', '10.5.4', '', '10', '', 'Are logs for external-facing technologies (for example, wireless, firewalls, DNS, mail) written onto a secure, centralized, internal log server or media?', '<ul><li>Interview system administrators</li><li>Examine system configurations and permissions</li></ul>'),
    (2281, 'YES_NO', '10.5.5', '', '10', '', 'Is file-integrity monitoring or change-detection software used on logs to ensure that existing log data cannot be changed without generating alerts (although new data being added should not cause an alert)?', '<ul><li>Examine settings, monitored files, and results from monitoring activities</li></ul>'),
    (2282, 'NONE', '10.6', '', '10', '', 'Are logs and security events for all system components reviewed to identify anomalies or suspicious activity as follows? Note: Log harvesting, parsing, and alerting tools may be used to achieve compliance with Requirement 10.6.', ''),
    (2283, 'YES_NO', '10.6.1.a', '', '10', '', 'Are written policies and procedures defined for reviewing the following at least daily, either manually or via log tools? All security events  Logs of all system components that store, process, or transmit CHD and/or SAD  Logs of all critical system components Logs of all servers and system components that perform security functions (for example, firewalls, intrusion-detection systems/intrusion-prevention systems (IDS/IPS), authentication servers, e-commerce redirection servers, etc.)', '<ul><li>Review security policies and procedures</li></ul>'),
    (2284, 'YES_NO', '10.6.1.b', '', '10', '', 'Are the above logs and security events reviewed at least daily?', '<ul><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2285, 'YES_NO', '10.6.2.a', '', '10', '', 'Are written policies and procedures defined for reviewing logs of all other system components periodically—either manually or via log tools—based on the organization’s policies and risk management strategy?', '<ul><li>Review security policies and procedures</li></ul>'),
    (2286, 'YES_NO', '10.6.2.b', '', '10', '', 'Are reviews of all other system components performed in accordance with organization’s policies and risk management strategy?', '<ul><li>Review risk assessment documentation</li><li>Interview personnel</li></ul>'),
    (2287, 'YES_NO', '10.6.3.a', '', '10', '', 'Are written policies and procedures defined for following up on exceptions and anomalies identified during the review process?', '<ul><li>Review security policies and procedures</li></ul>'),
    (2288, 'YES_NO', '10.6.3.b', '', '10', '', 'Is follow up to exceptions and anomalies performed?', '<ul><li>Observe processes</li><li>Interview personnel</li></ul>'),
    (2289, 'YES_NO', '10.7.a', '', '10', '', 'Are audit log retention policies and procedures in place and do they require that logs are retained for at least one year, with a minimum of three months immediately available for analysis (for example, online, archived, or restorable from backup)?', '<ul><li>Review security policies and procedures</li></ul>'),
    (2290, 'YES_NO', '10.7.b', '', '10', '', 'Are audit logs retained for at least one year?', '<ul><li>Interview personnel </li><li>Examine audit logs</li></ul>'),
    (2291, 'YES_NO', '10.7.c', '', '10', '', 'Are at least the last three months’ logs immediately available for analysis?', '<ul><li>Interview personnel</li><li>Observe processes</li></ul>'),
    (2292, 'NONE', '10.8', '', '10', '', 'This requirement applies only to service providers', ''),
    (2293, 'YES_NO', '10.9', '', '10', '', 'Are security policies and operational procedures for monitoring all access to network resources and cardholder data: Documented In use Known to all affected parties?', '<ul><li>Review security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2294, 'YES_NO', '11.1.a', '', '11', '', 'Are processes implemented for detection and identification of both authorized and unauthorized wireless access points on a quarterly basis?  Note: Methods that may be used in the process include, but are not limited to, wireless network scans, physical/logical inspections of system components and infrastructure, network access control (NAC), or wireless IDS/IPS. Whichever methods are used, they must be sufficient to detect and identify any unauthorized devices.', '<ul><li>Review policies and procedures</li></ul>'),
    (2295, 'YES_NO', '11.1.b', '', '11', '', 'Does the methodology detect and identify any unauthorized wireless access points, including at least the following? WLAN cards inserted into system components; Portable or mobile devices attached to system components to create a wireless access point (for example, by USB, etc.); and Wireless devices attached to a network port or network device.', '<ul><li>Evaluate the methodology</li></ul>'),
    (2296, 'YES_NO', '11.1.c', '', '11', '', 'If wireless scanning is utilized to identify authorized and unauthorized wireless access points, is the scan performed at least quarterly for all system components and facilities?', '<ul><li>Examine output from recent wireless scans</li></ul>'),
    (2297, 'YES_NO', '11.1.d', '', '11', '', 'If automated monitoring is utilized (for example, wireless IDS/IPS, NAC, etc.), is monitoring configured to generate alerts to notify personnel?', '<ul><li>Examine configuration settings</li></ul>'),
    (2298, 'YES_NO', '11.1.1', '', '11', '', 'Is an inventory of authorized wireless access points maintained and a business justification documented for all authorized wireless access points?', '<ul><li>Examine inventory records</li></ul>'),
    (2299, 'YES_NO', '11.1.2.a', '', '11', '', 'Does the incident response plan define and require a response in the event that an unauthorized wireless access point is detected?', '<ul><li>Examine incident response plan (see Requirement 12.10)</li></ul>'),
    (2300, 'YES_NO', '11.1.2.b', '', '11', '', 'Is action taken when unauthorized wireless access points are found?', '<ul><li>Interview responsible personnel</li><li>Inspect recent wireless scans and related responses</li></ul>'),
    (2301, 'NONE', '11.2', '', '11', '', 'Are internal and external network vulnerability scans run at least quarterly and after any significant change in the network (such as new system component installations, changes in network topology, firewall rule modifications, product upgrades), as follows? Note: Multiple scan reports can be combined for the quarterly scan process to show that all systems were scanned and all applicable vulnerabilities have been addressed.  Additional documentation may be required to verify non-remediated vulnerabilities are in the process of being addressed.   For initial PCI DSS compliance, it is not required that four quarters of passing scans be completed if the assessor verifies 1) the most recent scan result was a passing scan, 2) the entity has documented policies and procedures requiring quarterly scanning, and 3) vulnerabilities noted in the scan results have been corrected as shown in a re-scan(s). For subsequent years after the initial PCI DSS review, four quarters of passing scans must have occurred.', ''),
    (2302, 'YES_NO', '11.2.1.a', '', '11', '', 'Are quarterly internal vulnerability scans performed?', '<ul><li>Review scan reports</li></ul>'),
    (2303, 'YES_NO', '11.2.1.b', '', '11', '', 'Does the quarterly internal scan process address all “high risk” vulnerabilities and include rescans to verify all “high-risk” vulnerabilities (as defined in PCI DSS Requirement 6.1) are resolved?', '<ul><li>Review scan reports</li></ul>'),
    (2304, 'YES_NO', '11.2.1.c', '', '11', '', 'Are quarterly internal scans performed by a qualified internal resource(s) or qualified external third party, and if applicable, does organizational independence of the tester exist (not required to be a QSA or ASV)?', '<ul><li>Interview personnel</li></ul>'),
    (2305, 'YES_NO', '11.2.2.a', '', '11', '', 'Are quarterly external vulnerability scans performed? Note: Quarterly external vulnerability scans must be performed by an Approved Scanning Vendor (ASV), approved by the Payment Card Industry Security Standards Council (PCI SSC).  Refer to the ASV Program Guide published on the PCI SSC website for scan customer responsibilities, scan preparation, etc.', '<ul><li>Review results from the four most recent quarters of external vulnerability scans</li></ul>'),
    (2306, 'YES_NO', '11.2.2.b', '', '11', '', 'Do external quarterly scan and rescan results satisfy the ASV Program Guide requirements for a passing scan (for example, no vulnerabilities rated 4.0 or higher by the CVSS, and no automatic failures)?', '<ul><li>Review results of each external quarterly scan and rescan</li></ul>'),
    (2307, 'YES_NO', '11.2.2.c', '', '11', '', 'Are quarterly external vulnerability scans performed by a PCI SSC Approved Scanning Vendor (ASV?', '<ul><li>Review results of each external quarterly scan and rescan</li></ul>'),
    (2308, 'YES_NO', '11.2.3.a', '', '11', '', 'Are internal and external scans, and rescans as needed, performed after any significant change? Note: Scans must be performed by qualified personnel.', '<ul><li>Examine and correlate change control documentation and scan reports</li></ul>'),
    (2309, 'YES_NO', '11.2.3.b', '', '11', '', 'Does the scan process include rescans until:  For external scans, no vulnerabilities exist that are scored 4.0 or higher by the CVSS, For internal scans, a passing result is obtained or all “high-risk” vulnerabilities as defined in PCI DSS Requirement 6.1 are resolved?', '<ul><li>Review scan reports</li></ul>'),
    (2310, 'YES_NO', '11.2.3.c', '', '11', '', 'Are scans performed by a qualified internal resource(s) or qualified external third party, and if applicable, does organizational independence of the tester exist (not required to be a QSA or ASV)?', '<ul><li> Interview personnel</li></ul>'),
    (2311, 'YES_NO', '11.3', '', '11', '', 'Does the penetration-testing methodology include the following?  Is based on industry-accepted penetration testing approaches (for example, NIST SP800-115) Includes coverage for the entire CDE perimeter and critical systems Includes testing from both inside and outside the network Includes testing to validate any segmentation and scope-reduction controls Defines application-layer penetration tests to include, at a minimum, the vulnerabilities listed in Requirement 6.5 Defines network-layer penetration tests to include components that support network functions as well as operating systems Includes review and consideration of threats and vulnerabilities experienced in the last 12 months Specifies retention of penetration testing results and remediation activities results ', '<ul><li>Examine penetration-testing methodology</li><li>Interview responsible personnel</li></ul>'),
    (2312, 'YES_NO', '11.3.1.a', '', '11', '', 'Is external penetration testing performed per the defined methodology, at least annually, and after any significant infrastructure or application changes to the environment (such as an operating system upgrade, a sub-network added to the environment, or an added web server)?', '<ul><li>Examine scope of work</li><li>Examine results from the most recent external penetration test</li></ul>'),
    (2313, 'YES_NO', '11.3.1.b', '', '11', '', 'Are tests performed by a qualified internal resource or qualified external third party, and if applicable, does organizational independence of the tester exist (not required to be a QSA or ASV)?', '<ul><li>Interview responsible personnel</li></ul>'),
    (2314, 'YES_NO', '11.3.2.a', '', '11', '', 'Is internal penetration testing performed per the defined methodology, at least annually, and after any significant infrastructure or application changes to the environment (such as an operating system upgrade, a sub-network added to the environment, or an added web server)?', '<ul><li>Examine scope of work</li><li>Examine results from the most recent internal penetration test</li></ul>'),
    (2315, 'YES_NO', '11.3.2.b', '', '11', '', 'Are tests performed by a qualified internal resource or qualified external third party, and if applicable, does organizational independence of the tester exist (not required to be a QSA or ASV)?', '<ul><li>Interview responsible personnel</li></ul>'),
    (2316, 'YES_NO', '11.3.3', '', '11', '', 'Are exploitable vulnerabilities found during penetration testing corrected, followed by repeated testing to verify the corrections?', '<ul><li>Examine penetration testing results</li></ul>'),
    (2317, 'NONE', '11.3.4', '', '11', '', 'If segmentation is used to isolate the CDE from other networks: ', ''),
    (2318, 'YES_NO', '11.3.4.a', '', '11', '', 'Are penetration-testing procedures defined to test all segmentation methods, to confirm they are operational and effective, and isolate all out-of-scope systems from systems in the CDE? ', '<ul><li>Examine segmentation controls</li><li>Review penetration-testing methodology</li></ul>'),
    (2319, 'YES_NO', '11.3.4.b', '', '11', '', 'Does penetration testing to verify segmentation controls meet the following? Performed at least annually and after any changes to segmentation controls/methods Covers all segmentation controls/methods in use Verifies that segmentation methods are operational and effective, and isolate all out-of-scope systems from systems in the CDE.', '<ul><li>Examine results from the most recent penetration test</li></ul>'),
    (2320, 'YES_NO', '11.3.4.c', '', '11', '', 'Are tests performed by a qualified internal resource or qualified external third party, and if applicable, does organizational independence of the tester exist (not required to be a QSA or ASV)?', '<ul><li>Interview responsible personnel</li></ul>'),
    (2321, 'NONE', '11.3.4.1', '', '11', '', 'This requirement applies only to service providers', ''),
    (2322, 'YES_NO', '11.4.a', '', '11', '', 'Are intrusion-detection and/or intrusion-prevention techniques that detect and/or prevent intrusions into the network in place to monitor all traffic:  At the perimeter of the cardholder data environment, and At critical points in the cardholder data environment.', '<ul><li>Examine system configurations</li><li>Examine network diagrams</li></ul>'),
    (2323, 'YES_NO', '11.4.b', '', '11', '', 'Are intrusion-detection and/or intrusion-prevention techniques configured to alert personnel of suspected compromises?', '<ul><li>Examine system configurations</li><li>Interview responsible personnel</li></ul>'),
    (2324, 'YES_NO', '11.4.c', '', '11', '', 'Are all intrusion-detection and prevention engines, baselines, and signatures kept up-to-date?', '<ul><li>Examine IDS/IPS configurations</li><li>Examine vendor documentation</li></ul>'),
    (2325, 'YES_NO', '11.5.a', '', '11', '', 'Is a change-detection mechanism (for example, file-integrity monitoring tools) deployed to detect unauthorized modification (including changes, additions, and deletions) of critical system files, configuration files, or content files? Examples of files that should be monitored include: System executables Application executables Configuration and parameter files Centrally stored, historical or archived, log, and audit files  Additional critical files determined by entity (for example, through risk assessment or other means)', '<ul><li>Observe system settings and monitored files</li><li>Examine system configuration settings</li></ul>'),
    (2326, 'YES_NO', '11.5.b', '', '11', '', 'Is the change-detection mechanism configured to alert personnel to unauthorized modification (including changes, additions, and deletions) of critical system files, configuration files or content files, and do the tools perform critical file comparisons at least weekly? Note: For change detection purposes, critical files are usually those that do not regularly change, but the modification of which could indicate a system compromise or risk of compromise. Change detection mechanisms such as file-integrity monitoring products usually come pre-configured with critical files for the related operating system. Other critical files, such as those for custom applications, must be evaluated and defined by the entity (that is the merchant or service provider).', '<ul><li>Observe system settings and monitored files</li><li>Review results from monitoring activities</li></ul>'),
    (2327, 'YES_NO', '11.5.1', '', '11', '', 'Is a process in place to respond to any alerts generated by the change-detection solution?', '<ul><li>Examine system configuration settings</li></ul>'),
    (2328, 'YES_NO', '11.6', '', '11', '', 'Are security policies and operational procedures for security monitoring and testing: Documented In use Known to all affected parties?', '<ul><li>Examine security policies and operational procedures</li><li>Interview personnel</li></ul>'),
    (2329, 'YES_NO', '12.1', '', '12', '', 'Is a security policy established, published, maintained, and disseminated to all relevant personnel? ', '<ul><li>Review the information security policy</li></ul>'),
    (2330, 'YES_NO', '12.1.1', '', '12', '', 'Is the security policy reviewed at least annually and updated when the environment changes?', '<ul><li>Review the information security policy</li><li>Interview responsible personnel</li></ul>'),
    (2331, 'YES_NO', '12.2.a', '', '12', '', 'Is an annual risk assessment process implemented that  Identifies critical assets, threats, and vulnerabilities, and Results in a formal, documented analysis of risk? Examples of risk assessment methodologies include but are not limited to OCTAVE, ISO 27005 and NIST SP 800-30.', '<ul><li>Review annual risk assessment process</li><li>Interview personnel</li></ul>'),
    (2332, 'YES_NO', '12.2.b', '', '12', '', 'Is the risk assessment process performed at least annually and upon significant changes to the environment (for example, acquisition, merger, relocation, etc.)?', '<ul><li>Review risk assessment documentation</li><li>Interview responsible personnel</li></ul>'),
    (2333, 'NONE', '12.3', '', '12', '', 'Are usage policies for critical technologies developed to define proper use of these technologies and require the following: Note: Examples of critical technologies include, but are not limited to, remote access and wireless technologies, laptops, tablets, removable electronic media, e-mail usage and Internet usage.', ''),
    (2334, 'YES_NO', '12.3.1', '', '12', '', 'Explicit approval by authorized parties to use the technologies?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2335, 'YES_NO', '12.3.2', '', '12', '', 'Authentication for use of the technology?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2336, 'YES_NO', '12.3.3', '', '12', '', 'A list of all such devices and personnel with access?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2337, 'YES_NO', '12.3.4', '', '12', '', 'A method to accurately and readily determine owner, contact information, and purpose (for example, labeling, coding, and/or inventorying of devices)?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2338, 'YES_NO', '12.3.5', '', '12', '', 'Acceptable uses of the technologies?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2339, 'YES_NO', '12.3.6', '', '12', '', 'Acceptable network locations for the technologies?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2340, 'YES_NO', '12.3.7', '', '12', '', 'List of company-approved products?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2341, 'YES_NO', '12.3.8', '', '12', '', 'Automatic disconnect of sessions for remote-access technologies after a specific period of inactivity?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2342, 'YES_NO', '12.3.9', '', '12', '', 'Activation of remote-access technologies for vendors and business partners only when needed by vendors and business partners, with immediate deactivation after use?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2343, 'YES_NO', '12.3.10.a', '', '12', '', 'For personnel accessing cardholder data via remote-access technologies, does the policy specify the prohibition of copying, moving, and storage of cardholder data onto local hard drives and removable electronic media, unless explicitly authorized for a defined business need?  Where there is an authorized business need, the usage policies must require the data be protected in accordance with all applicable PCI DSS Requirements.', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2344, 'YES_NO', '12.3.10.b', '', '12', '', 'For personnel with proper authorization, does the policy require the protection of cardholder data in accordance with PCI DSS Requirements?', '<ul><li>Review usage policies</li><li>Interview responsible personnel</li></ul>'),
    (2345, 'YES_NO', '12.4', '', '12', '', 'Do security policy and procedures clearly define information security responsibilities for all personnel?', '<ul><li>Review information security policy and procedures</li><li>Interview a sample of responsible personnel</li></ul>'),
    (2346, 'NONE', '12.4.1', '', '12', '', 'This requirement applies only to service providers', ''),
    (2347, 'YES_NO', '12.5.a', '', '12', '', 'Is responsibility for information security formally assigned to a Chief Security Officer or other security-knowledgeable member of management?', '<ul><li>Review information security policy and procedures</li></ul>'),
    (2348, 'NONE', '12.5.b', '', '12', '', 'Are the following information security management responsibilities formally assigned to an individual or team:', ''),
    (2349, 'YES_NO', '12.5.1', '', '12', '', 'Establishing, documenting, and distributing security policies and procedures?', '<ul><li>Review information security policy and procedures</li></ul>'),
    (2350, 'YES_NO', '12.5.2', '', '12', '', 'Monitoring and analyzing security alerts and information, and distributing to appropriate personnel?', '<ul><li>Review information security policy and procedures</li></ul>'),
    (2351, 'YES_NO', '12.5.3', '', '12', '', 'Establishing, documenting, and distributing security incident response and escalation procedures to ensure timely and effective handling of all situations?', '<ul><li>Review information security policy and procedures</li></ul>'),
    (2352, 'YES_NO', '12.5.4', '', '12', '', 'Administering user accounts, including additions, deletions, and modifications?', '<ul><li>Review information security policy and procedures</li></ul>'),
    (2353, 'YES_NO', '12.5.5', '', '12', '', 'Monitoring and controlling all access to data?', '<ul><li>Review information security policy and procedures</li></ul>'),
    (2354, 'YES_NO', '12.6.a', '', '12', '', 'Is a formal security awareness program in place to make all personnel aware of the cardholder data security policy and procedures?', '<ul><li>Review security awareness program</li></ul>'),
    (2355, 'NONE', '12.6.b', '', '12', '', 'Do security awareness program procedures include the following:', ''),
    (2356, 'YES_NO', '12.6.1.a', '', '12', '', 'Does the security awareness program provide multiple methods of communicating awareness and educating personnel (for example, posters, letters, memos, web based training, meetings, and promotions)? Note:  Methods can vary depending on the role of the personnel and their level of access to the cardholder data.', '<ul><li>Review security awareness program</li><li>Review security awareness program procedures</li><li>Review security awareness program attendance records</li></ul>'),
    (2357, 'YES_NO', '12.6.1.b', '', '12', '', 'Are personnel educated upon hire and at least annually?', '<ul><li>Examine security awareness program procedures and documentation</li></ul>'),
    (2358, 'YES_NO', '12.6.1.c', '', '12', '', 'Have employees completed awareness training and are they aware of the importance of cardholder data security?', '<ul><li>Interview personnel</li></ul>'),
    (2359, 'YES_NO', '12.6.2', '', '12', '', 'Are personnel required to acknowledge at least annually that they have read and understood the security policy and procedures?', '<ul><li>Examine security awareness program procedures and documentation</li></ul>'),
    (2360, 'YES_NO', '12.7', '', '12', '', 'Are potential personnel (see definition of “personnel” above) screened prior to hire to minimize the risk of attacks from internal sources?  Examples of background checks include previous employment history, criminal record, credit history and reference checks. Note: For those potential personnel to be hired for certain positions, such as store cashiers who only have access to one card number at a time when facilitating a transaction, this requirement is a recommendation only.', '<ul><li>Interview Human Resource department management</li></ul>'),
    (2361, 'NONE', '12.8', '', '12', '', 'Are policies and procedures maintained and implemented to manage service providers with whom cardholder data is shared, or that could affect the security of cardholder data, as follows:', ''),
    (2362, 'YES_NO', '12.8.1', '', '12', '', 'Is a list of service providers maintained, including a description of the service(s) provided?', '<ul><li>Review policies and procedures</li><li>Observe processes</li><li>Review list of service providers </li></ul>'),
    (2363, 'YES_NO', '12.8.2', '', '12', '', 'Is a written agreement maintained that includes an acknowledgement that the service providers are responsible for the security of cardholder data the service providers possess or otherwise store, process, or transmit on behalf of the customer, or to the extent that they could impact the security of the customer’s cardholder data environment? Note: The exact wording of an acknowledgement will depend on the agreement between the two parties, the details of the service being provided, and the responsibilities assigned to each party. The acknowledgement does not have to include the exact wording provided in this requirement.', '<ul><li>Observe written agreements</li><li>Review policies and procedures </li></ul>'),
    (2364, 'YES_NO', '12.8.3', '', '12', '', 'Is there an established process for engaging service providers, including proper due diligence prior to engagement?', '<ul><li>Observe processes</li><li>Review policies and procedures and supporting documentation</li></ul>'),
    (2365, 'YES_NO', '12.8.4', '', '12', '', 'Is a program maintained to monitor service providers’ PCI DSS compliance status at least annually?', '<ul><li>Observe processes</li><li>Review policies and procedures and supporting documentation</li></ul>'),
    (2366, 'YES_NO', '12.8.5', '', '12', '', 'Is information maintained about which PCI DSS requirements are managed by each service provider, and which are managed by the entity?', '<ul><li>Observe processes</li><li>Review policies and procedures and supporting documentation</li></ul>'),
    (2367, 'NONE', '12.9', '', '12', '', 'This requirement applies only to service providers. ', ''),
    (2368, 'NONE', '12.10', '', '12', '', 'Has an incident response plan been implemented in preparation to respond immediately to a system breach, as follows:', ''),
    (2369, 'YES_NO', '12.10.1.a', '', '12', '', 'Has an incident response plan been created to be implemented in the event of system breach?', '<ul><li>Review the incident response plan</li><li>Review incident response plan procedures</li></ul>'),
    (2370, 'NONE', '12.10.1.b', '', '12', '', 'Does the plan address the following, at a minimum: ', ''),
    (2371, 'YES_NO', '12.10.1.c', '', '12', '', 'Roles, responsibilities, and communication and contact strategies in the event of a compromise including notification of the payment brands, at a minimum?', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2372, 'YES_NO', '12.10.1.d', '', '12', '', 'Specific incident response procedures?', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2373, 'YES_NO', '12.10.1.e', '', '12', '', 'Business recovery and continuity procedures?', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2374, 'YES_NO', '12.10.1.f', '', '12', '', 'Data backup processes?', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2375, 'YES_NO', '12.10.1.g', '', '12', '', 'Analysis of legal requirements for reporting compromises? ', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2376, 'YES_NO', '12.10.1.h', '', '12', '', 'Coverage and responses of all critical system components?', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2377, 'YES_NO', '12.10.1.i', '', '12', '', 'Reference or inclusion of incident response procedures from the payment brands?', '<ul><li>Review incident response plan procedures</li></ul>'),
    (2378, 'YES_NO', '12.10.2', '', '12', '', 'Is the plan reviewed and tested at least annually, including all elements listed in Requirement 12.10.1?', '<ul><li>Review incident response plan procedures </li><li>Interview responsible personnel</li></ul>'),
    (2379, 'YES_NO', '12.10.3', '', '12', '', 'Are specific personnel designated to be available on a 24/7 basis to respond to alerts?', '<ul><li>Observe processes</li><li>Review policies</li><li>Interview responsible personnel</li></ul>'),
    (2380, 'YES_NO', '12.10.4', '', '12', '', 'Is appropriate training provided to staff with security breach response responsibilities?', '<ul><li>Observe processes</li><li>Review incident response plan procedures </li><li>Interview responsible personnel</li></ul>'),
    (2381, 'YES_NO', '12.10.5', '', '12', '', 'Are alerts from security monitoring systems included in the incident response plan?', '<ul><li>Observe processes</li><li>Review incident response plan procedures </li></ul>'),
    (2382, 'YES_NO', '12.10.6', '', '12', '', 'Is a process developed and in place to modify and evolve the incident response plan according to lessons learned and to incorporate industry developments?', '<ul><li>Observe processes</li><li>Review incident response plan procedures </li><li>Interview responsible personnel</li></ul>'),
    (2383, 'NONE', '12.11', '', '12', '', 'This requirement applies only to service providers', ''),
    (2384, 'YES_NO', 'A2.1', '', 'A2', '', '', ''),
    (2385, 'NONE', 'A2.2', '', 'A2', '', 'This requirement applies only to service providers', ''),
